h2=h2+(above(treb,1.32)*0.8)*.9;
vr=sin(h2*.8)*.5+.5;
vg=sin(h2*.5)*.5+.5;
vb=sin(h2*.1)*.5+.5;

g=vg;
r=vr;
b=vb;
g2=g;
r2=r;
b2=b;
dist=(frame%100)*.01;
maat=maat+(above(bass_att,1.5));
maat=below(maat,16)*maat;
